package org.ip.sesion01;

public class Distancia {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int co1,co2;
		double resul;
		co1=2;
		co2=5;
		resul=Math.sqrt(Math.pow((0-co2),2)+ Math.pow((0-co1),2));
		System.out.print("La distancia del punto ("+ co1+"," + co2 + ") al punto (0,0) es "+ resul);
				

	}

}
