package org.ip.sesion01;

public class DivisionPorCero {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double x=19.0;
		
		System.out.print("EJEMPLOS DE DIVISIONES POR CERO CON ENTEROR Y REALES \n\n");
		System.out.print(x+" / 0.0 = " +x/0 + "\n" );
		System.out.print(x+" % 0.0 = " +x%0 + "\n");
		System.out.println((int)x+" / 0 =");
		System.out.println(x+" / 0 = "+ (int) +x/0);
		
		
	}

}




























